mapper.select:
select t1.order_no as orderNo, t1.risk_point as riskPoint, t1.order_note as orderNote, t1.order_num as orderNum, t1.trx_order_id as trxOrderId, t1.audit_result as auditResult, t1.audit_note as auditNote, t1.file_name as fileName, t1.status as status, t1.create_time as createTime, t2.order_serial_num as orderSerialNum, t2.payee_name as name, t1.id as id, t1.feed_back_comment as feedBackComment, t1.feed_back_time as feedBackTime from tbl_order_sheet t1, tbl_trx_order t2 where 1=1 and t1.trx_order_id = t2.id and t1.merchant_id = #{merchantId}
and t1.order_no like concat('%',#{orderNo},'%')
and t2.payee_name like concat('%',#{name},'%')
and t2.order_serial_num like concat('%',#{orderSerialNum},'%')
and t1.status = #{status}
and t1.audit_result = #{auditResult}
and t1.create_time between concat(#{startDate},' 00:00:00') and concat(#{endDate},' 23:59:59')
order by t1.create_time desc limit #{startIndex},#{pageSize}

select count(0) from tbl_order_sheet t1, tbl_trx_order t2 where 1=1 and t1.trx_order_id = t2.id and t1.merchant_id = #{merchantId}
and t1.order_no like concat('%',#{orderNo},'%')
and t2.payee_name like concat('%',#{name},'%')
and t2.order_serial_num like concat('%',#{orderSerialNum},'%')
and t1.status = #{status}
and t1.audit_result = #{auditResult}
and t1.create_time between concat(#{startDate},' 00:00:00') and concat(#{endDate},' 23:59:59')
